(function() {
  
    'use strict';
    
    var isDrawing, lastPoint;
    var container16    = document.getElementById('js-container16'),
        canvas16       = document.getElementById('js-canvas16'),
        canvasWidth16  = canvas16.width,
        canvasHeight16 = canvas16.height,
        ctx6          = canvas16.getContext('2d'),
        image16        = new Image(),
        brush16        = new Image();
        
    // base64 Workaround because Same-Origin-Policy
    image16.src = 'data:image/png;base64,';
    image16.onload = function() {
      ctx6.drawImage(image16, 0, 0);
      // Show the form when Image is loaded.
      document.getElementById('form16').style.visibility = 'visible';
    };
    brush16.src = 'data:image/png;base64,';
    
    canvas16.addEventListener('mousedown', handleMouseDown, false);
    canvas16.addEventListener('touchstart', handleMouseDown, false);
    canvas16.addEventListener('mousemove', handleMouseMove, false);
    canvas16.addEventListener('touchmove', handleMouseMove, false);
    canvas16.addEventListener('mouseup', handleMouseUp, false);
    canvas16.addEventListener('touchend', handleMouseUp, false);
    
    function distanceBetween(point1, point2) {
      return Math.sqrt(Math.pow(point2.x - point1.x, 2) + Math.pow(point2.y - point1.y, 2));
    }
    
    function angleBetween(point1, point2) {
      return Math.atan2( point2.x - point1.x, point2.y - point1.y );
    }
    
    // Only test every `stride` pixel. `stride`x faster,
    // but might lead to inaccuracy
    function getFilledInPixels(stride) {
      if (!stride || stride < 1) { stride = 1; }
      
      var pixels   = ctx6.getImageData(0, 0, canvasWidth16, canvasHeight16),
          pdata    = pixels.data,
          l        = pdata.length,
          total    = (l / stride),
          count    = 0;
      
      // Iterate over all pixels
      for(var i = count = 0; i < l; i += stride) {
        if (parseInt(pdata[i]) === 0) {
          count++;
        }
      }
      
      return Math.round((count / total) * 100);
    }
    
    function getMouse(e, canvas16) {
      var offsetX = 0, offsetY = 0, mx, my;
  
      if (canvas16.offsetParent !== undefined) {
        do {
          offsetX += canvas16.offsetLeft;
          offsetY += canvas16.offsetTop;
        } while ((canvas16 = canvas16.offsetParent));
      }
  
      mx = (e.pageX || e.touches[0].clientX) - offsetX;
      my = (e.pageY || e.touches[0].clientY) - offsetY;
  
      return {x: mx, y: my};
    }
    
    function handlePercentage(filledInPixels) {
      filledInPixels = filledInPixels || 0;
      console.log(filledInPixels + '%');
      if (filledInPixels > 50) {
        canvas16.parentNode.removeChild(canvas16);
      }
    }
    
    function handleMouseDown(e) {
      isDrawing = true;
      lastPoint = getMouse(e, canvas16);
    }
  
    function handleMouseMove(e) {
      if (!isDrawing) { return; }
      
      e.preventDefault();
  
      var currentPoint = getMouse(e, canvas16),
          dist = distanceBetween(lastPoint, currentPoint),
          angle = angleBetween(lastPoint, currentPoint),
          x, y;
      
      for (var i = 0; i < dist; i++) {
        x = lastPoint.x + (Math.sin(angle) * i) - 25;
        y = lastPoint.y + (Math.cos(angle) * i) - 25;
        ctx6.globalCompositeOperation = 'destination-out';
        ctx6.drawImage(brush16, x, y);
      }
      
      lastPoint = currentPoint;
      handlePercentage(getFilledInPixels(32));
    }
  
    function handleMouseUp(e) {
      isDrawing = false;
    }
    
  })();